    return "let me introduce："+who;
}
export hello;

// foo.js
import hello from "bar";
var hungry="hippo";
function  awesome() {
    console.log(hello(hungry).toUpperCase());
}
export awesome;

// baz.js
module foo from "foo";
module bar from "bar";

console.log(
  bar.hello("rhino")  
);
